mapper.select:
SELECT cu.user_id, cu.id AS 'id', cu.user_name AS 'name', IF(ua.file_group IS NULL ,NULL,CONCAT(#{url,jdbcType=VARCHAR},ua.file_group,'/',ua.attach_path)) AS 'img', mu.user_name FROM maoding_im_group_member tu LEFT JOIN maoding_web_user mu on mu.id=tu.account_id LEFT JOIN maoding_web_company_user cu ON (tu.account_id = cu.user_id
AND tu.org_id = cu.company_id
) LEFT JOIN maoding_web_user_attach ua ON ( ua.user_id = cu.user_id AND ua.attach_type = 5 ) WHERE tu.org_id = #{orgId,jdbcType=VARCHAR} AND cu.departure_time IS NULL AND cu.audit_status='1' GROUP BY tu.account_id

SELECT a.user_id , a.id  AS 'id', a.user_name AS 'name',
 IF(ua.file_group IS NULL ,NULL,CONCAT(#{url,jdbcType=VARCHAR},ua.file_group,'/',ua.attach_path)) AS 'img',
 u.user_name
 FROM maoding_im_group_member tu
 LEFT JOIN maoding_web_user u ON u.id=tu.account_id
 LEFT JOIN  maoding_web_company_user a ON tu.account_id = a.user_id AND a.company_id = #{companyId,jdbcType=VARCHAR}
 LEFT JOIN maoding_web_user_attach ua ON ( ua.user_id = a.user_id AND ua.attach_type = 5
 )
 WHERE  tu.org_id = #{orgId,jdbcType=VARCHAR}
 AND a.audit_status='1'
 
   AND a.user_id = #{userId,jdbcType=VARCHAR}
 
 group by tu.account_id
