mapper.select:
select

from maoding_web_company_attach
where 1=1

	and company_id = #{companyId,jdbcType=VARCHAR}


	and file_type=#{fileType,jdbcType=VARCHAR}

order by seq

mapper.delete:
		delete from maoding_web_company_attach
		where 1=1
		
			and company_id = #{companyId,jdbcType=VARCHAR}
		
		
			and file_type=#{fileType,jdbcType=VARCHAR}
		

		
			and seq=#{seq,jdbcType=VARCHAR}

mapper.insert:
insert into maoding_web_company_attach (id, company_id, file_name,seq,
file_type, file_path, file_group,create_date,
create_by, update_date, update_by
)
values (#{id,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR},
#{fileName,jdbcType=VARCHAR},#{seq,jdbcType=INTEGER},
#{fileType,jdbcType=VARCHAR}, #{filePath,jdbcType=VARCHAR}, #{fileGroup,jdbcType=VARCHAR}, now(),
#{createBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}
)
