mapper.select:
select
GROUP_CONCAT(b.task_name)
from maoding_web_project_member a
LEFT JOIN maoding_web_project_task b on a.target_id = b.id
where b.task_status='0' AND a.deleted=0
and a.company_id = #{companyId,jdbcType=VARCHAR}
and a.project_id =  #{projectId,jdbcType=VARCHAR}

  and a.target_id = #{companyUserId,jdbcType=VARCHAR}

order by a.create_date

select
count(1)
from maoding_web_project_task_responsibler a
where a.type=2
and a.company_id = #{companyId,jdbcType=VARCHAR}
and a.project_id =  #{projectId,jdbcType=VARCHAR}

select
GROUP_CONCAT(target_id)
from maoding_web_project_task_responsibler a
where a.type=1
and a.company_id = #{companyId,jdbcType=VARCHAR}
and a.project_id =  #{projectId,jdbcType=VARCHAR}

select
GROUP_CONCAT(target_id) as targetIds,a.task_id as taskId
from maoding_web_project_task_responsibler a
where 1=1
and a.company_id = #{companyId,jdbcType=VARCHAR}
and a.project_id =  #{projectId,jdbcType=VARCHAR}

  and a.target_id = #{companyUserId,jdbcType=VARCHAR}

and a.task_id in

  #{taskId}
