mapper.insert:
insert into maoding_web_project_fee (id, project_id, point_id, 
  type, fee, invoice_number, status,
  fee_date, remark, create_date, 
  create_by, update_date, update_by
  )
values (#{id,jdbcType=VARCHAR}, #{projectId,jdbcType=VARCHAR}, #{pointId,jdbcType=VARCHAR}, 
  #{type,jdbcType=VARCHAR}, #{fee,jdbcType=DECIMAL}, #{invoiceNumber,jdbcType=VARCHAR}, '0',
  #{feeDate,jdbcType=DATE}, #{remark,jdbcType=VARCHAR}, now(), 
  #{createBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}
  )

mapper.select:
 select 
 
 from maoding_web_project_fee
 where 1=1 and status='0'
 
 and point_id = #{pointId,jdbcType=VARCHAR}
 
	
 and type = #{type,jdbcType=VARCHAR}
 
  order by fee_date
