mapper.select:
select
COUNT(1)+1
from maoding_web_project_process where task_manage_id = #{taskManageId,jdbcType=VARCHAR}

SELECT

FROM maoding_web_project_process
WHERE status='0'

 and task_manage_id = #{taskManageId,jdbcType=VARCHAR}


  and project_id = #{projectId,jdbcType=VARCHAR}


  and task_manage_id != #{withOutTaskManagerId,jdbcType=VARCHAR}

mapper.insert:
