  create_by, update_date, update_by
  )
values (#{id,jdbcType=VARCHAR}, #{processName,jdbcType=VARCHAR}, #{companyId,jdbcType=VARCHAR}, 
  #{projectId,jdbcType=VARCHAR}, #{taskManageId,jdbcType=VARCHAR},'0', #{seq,jdbcType=INTEGER},now(),
  #{createBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}
  )

mapper.update:
update maoding_web_project_process
set status = #{status,jdbcType=INTEGER}
where task_manage_id = #{taskManageId,jdbcType=VARCHAR}
