mapper.select:
SELECT

from maoding_web_project_cost
where status= '0'
and project_id=#{projectId,jdbcType=VARCHAR}

  and type = #{type,jdbcType=VARCHAR}


  and from_company_id = #{fromCompanyId,jdbcType=VARCHAR}


  and to_company_id = #{toCompanyId,jdbcType=VARCHAR}


  and id = #{costId,jdbcType=VARCHAR}


  and flag = #{flag,jdbcType=VARCHAR}

order by create_date desc;

SELECT b.id,b.fee_description,b.fee_proportion,b.fee,b.type,c.id as point_detail_id,c.fee as point_detail_fee,
if(f.nick_name is null or f.nick_name='',e.company_name,f.nick_name) as company_name,

  ,d.fee as payment_fee

from maoding_web_project_cost a
LEFT JOIN maoding_web_project_cost_point b on a.id = b.cost_id
LEFT JOIN maoding_web_project_cost_point_detail c on b.id = c.point_id

  LEFT JOIN maoding_web_project_cost_payment_detail d on c.id = d.point_detail_id

LEFT JOIN maoding_web_company e on (a.from_company_id = e.id or a.to_company_id = e.id) and e.id!= #{companyId,jdbcType=VARCHAR}
LEFT JOIN maoding_web_business_partner f on f.company_id = e.id
where 1=1

  AND c.id = #{pointDetailId,jdbcType=VARCHAR}


  AND d.id = #{paymentDetailId,jdbcType=VARCHAR}

SELECT sum(ifnull(if(c.fee_status != 2,c.fee,0),0)) as detailFee, sum(ifnull(if(c.fee_status != 2 and c.paid_fee_status = 1,c.fee,0),0)) as detailPaidFee, sum(ifnull(if(c.fee_status = 1,c.fee,0),0)) as detailPayFee, sum(ifnull(d.paid_fee,0)) as sumPaidFee, sum(ifnull(d.pay_fee,0)) as sumPayFee from maoding_web_project_cost_point b INNER JOIN maoding_web_project_cost_point_detail c on b.id = c.point_id and c.`status` = 0 LEFT JOIN (SELECT sum(if(pay_date is not null,fee,0)) as pay_fee, sum(if(paid_date is not null,fee,0))as paid_fee,point_detail_id from maoding_web_project_cost_payment_detail f where project_id = #{projectId} and `status` = 0 GROUP BY point_detail_id) d on c.id = d.point_detail_id where b.cost_id= #{costId} and b.`status`=0
and b.id = #{pointId}
and (c.company_id = #{companyId} or c.relation_company_id = #{companyId})

SELECT b.id, b.fee_description, b.fee_proportion, b.fee, count(DISTINCT c.id) as pointDetailCount, sum(ifnull(if(c.fee_status != 2,c.fee,0),0)) as detailFee, sum(ifnull(if(c.fee_status != 2 and c.paid_fee_status = 1,c.fee,0),0)) as detailPaidFee, sum(ifnull(if(c.fee_status = 1,c.fee,0),0)) as detailPayFee, sum(ifnull(d.paid_fee,0)) as sumPaidFee, sum(ifnull(d.pay_fee,0)) as sumPayFee from maoding_web_project_cost_point b LEFT JOIN maoding_web_project_cost_point_detail c on b.id = c.point_id and c.`status` = 0 and (c.company_id = #{companyId} or c.relation_company_id = #{companyId}) LEFT JOIN (SELECT sum(if(pay_date is not null,fee,0)) as pay_fee, sum(if(paid_date is not null,fee,0))as paid_fee,point_detail_id from maoding_web_project_cost_payment_detail f where project_id = #{projectId} and `status` = 0 GROUP BY point_detail_id) d on c.id = d.point_detail_id where b.cost_id= #{costId} and b.`status`=0
and b.id = #{pointId}
and c.id = #{pointDetailId}
group by b.id order by b.create_date

SELECT b.id, b.fee_description, b.fee_proportion, b.fee, c.id as point_detail_id, f.user_name as userName, count(DISTINCT c.id) as pointDetailCount, sum(ifnull(if(c.fee_status != 2,c.fee,0),0)) as detailFee, sum(ifnull(if(c.fee_status != 2 and c.paid_fee_status = 1,c.fee,0),0)) as detailPaidFee, sum(ifnull(if(c.fee_status = 1,c.fee,0),0)) as detailPayFee, sum(ifnull(d.paid_fee,0)) as sumPaidFee, sum(ifnull(d.pay_fee,0)) as sumPayFee from maoding_web_project_cost_point b INNER JOIN maoding_web_project_cost_point_detail c on b.id = c.point_id and c.`status` = 0 and (c.company_id = #{companyId} or c.relation_company_id = #{companyId}) LEFT JOIN (SELECT sum(if(pay_date is not null,fee,0)) as pay_fee, sum(if(paid_date is not null,fee,0))as paid_fee,point_detail_id from maoding_web_project_cost_payment_detail f where project_id = #{projectId} and `status` = 0 GROUP BY point_detail_id) d on c.id = d.point_detail_id left join maoding_web_project_cost_operater e on e.cost_detail_id = c.id and e.type = 1 left join maoding_web_company_user f on e.company_user_id = f.id where b.cost_id= #{costId} and b.`status`=0
and b.id = #{pointId}
order by b.create_date

SELECT c.id, c.fee, c.fee_status as feeStatus, c.point_detail_description as pointDetailDescription, c.invoice, DATE_FORMAT(c.create_date,'%Y-%m-%d') as pointDetailDate, f.user_name as userName, sum(ifnull(if(c.fee_status != 2,c.fee,0),0)) as detailFee, sum(ifnull(if(c.fee_status != 2 and c.paid_fee_status = 1,c.fee,0),0)) as detailPaidFee, sum(ifnull(if(c.fee_status = 1,c.fee,0),0)) as detailPayFee, sum(ifnull(d.paid_fee,0)) as sumPaidFee, sum(ifnull(d.pay_fee,0)) as sumPayFee FROM maoding_web_project_cost_point_detail c LEFT JOIN (SELECT sum(if(pay_date is not null,fee,0)) as pay_fee, sum(if(paid_date is not null,fee,0))as paid_fee,point_detail_id from maoding_web_project_cost_payment_detail f where project_id = #{projectId} and `status` = 0 GROUP BY point_detail_id) d on c.id = d.point_detail_id left join maoding_web_project_cost_operater e on e.cost_detail_id = c.id and e.type = 1 left join maoding_web_company_user f on e.company_user_id = f.id where 1=1
and c.point_id= #{pointId}
and c.id = #{pointDetailId}
and c.`status` = 0 and (c.company_id = #{companyId} or c.relation_company_id = #{companyId}) group by c.id order by c.create_date

SELECT b.id, b.fee_description, b.fee, d.pay_date, d.paid_date, f.user_name as userName, d.id as paymentId, d.fee as paymentFee from maoding_web_project_cost_point b INNER JOIN maoding_web_project_cost_point_detail c on b.id = c.point_id INNER JOIN maoding_web_project_cost_payment_detail d on c.id = d.point_detail_id
left join maoding_web_project_cost_operater e on e.cost_detail_id = d.id and e.type = 5 left join maoding_web_company_user f on e.company_user_id = f.id
left join maoding_web_project_cost_operater e on e.cost_detail_id = d.id and e.type = 6 left join maoding_web_company_user f on e.company_user_id = f.id
where b.cost_id = #{costId}
and c.point_id= #{pointId}
and c.id = #{pointDetailId}
and b.`status` = 0 and c.`status` = 0 and d.`status` = 0 and (c.company_id = #{companyId} or c.relation_company_id = #{companyId}) order by c.create_date
