mapper.select:
select distinct(a.id),a.code,a.name,a.pid,a.root_id,a.seq, b.id as child_id,b.code as child_code,b.name as child_name,b.pid as child_pid,b.root_id as child_root_id,b.seq as child_seq,c.permission_id,c.type from maoding_web_permission a LEFT JOIN maoding_web_permission b on a.id = b.pid LEFT JOIN ( SELECT DISTINCT permission_id,'2' AS type FROM maoding_web_user_permission up WHERE user_id = #{userId,jdbcType=VARCHAR} AND company_id = #{companyId,jdbcType=VARCHAR} ) c ON b.id = c.permission_id WHERE a.pid IS NULL
and c.type = #{type,jdbcType=VARCHAR}
GROUP BY child_id ORDER BY a.seq,b.seq
